import React, { useEffect, useState } from 'react';
import './App.css';

import scale from './assets/radaltback.png';
import cover from './assets/radaltcover.png';
import needle from './assets/radaltneedle.png';
import bugImg from './assets/bug.png';
import flagImg from './assets/radaltflag.png';

function App() {
  const [hasPower, setHasPower] = useState(false)
  const [imageDiameter, setImageDiameter] = useState(0)
  const [altitude, setAltitude] = useState(0)
  const [altitudeWithoutPower, setAltitudeWithoutPower] = useState(0)
  const [bugs, setBugs] = useState(0)

  const toggleHasPower = () => {
    if (!hasPower) {
      setAltitude(altitudeWithoutPower)
    }
    setHasPower(!hasPower)
  }

  const initialiseImageDiameter = ({ target: img }) => {
    setImageDiameter(img.width)
  }

  const updateAltitude = () => {
    if (hasPower) {
      setAltitude(document.getElementById('altitude').value)
    } else {
      setAltitudeWithoutPower(document.getElementById('altitude').value)
    }
  }

  const updateBugs = () => {
    setBugs(document.getElementById('bugs').value)
  }

  const displayFlag = () => {
    if (!hasPower) {
      return <img className='image_pos' alt='' src={flagImg} />
    }
  }

  return (
    <div>
      <h1>Radar Altimeter</h1>

      <img className='image_pos' onLoad={initialiseImageDiameter} alt='' src={scale} />
      <img className='image_pos' alt='' src={cover} />
      <img className='image_pos' alt='' src={needle} />
      <img className='image_pos' alt='' src={bugImg} />
      {displayFlag()}

      <div className='controls'>
        <div>
          <label>Altitude:</label>
          <input type='range' min='0' max='1500' id='altitude' onChange={updateAltitude}></input>
        </div>
          <div>
            <label>Bugs:</label>
            <input type='range' min='0' max='1500' id='bugs' onChange={updateBugs}></input>
          </div>
        <div>
          <label>Has Power:</label>
          <input type='checkbox' id='hasPower' name='hasPower' value={hasPower} onClick={toggleHasPower}/>
        </div>
      </div>
    </div>
  );
}

export default App;
